import React from 'react';
import logo from './logo.svg';
import DetailsForm from './DetailsForm';
import './App.css';
import { throwStatement } from '@babel/types';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      clientId: '',
      clientSecret: '',
      authEndpoint: '',
      tokenEndpoint: ''
    };

    this.updateState = this.updateState.bind(this);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>Client ID: {this.state.clientId}</p>
          <p>Client Secret: {this.state.clientSecret}</p>
          <p>Auth Endpoint: {this.state.authEndpoint}</p>
          <p>Token Endpoint: {this.state.tokenEndpoint}</p>

          <DetailsForm changeHandler={this.updateState}/>

        </header>
      </div>
    );
  }

  updateState(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });

    console.log(this.state)
  }
}

export default App;
