    return (
      <div className="App">
        <header className="App-header"/>
        <body>
          <div className="container center">
            {main}
          </div>
        </body>
      </div>
    );
  }

  updateState(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }

  nextStage() {
    if (this.state.stage == 0) {
      console.log("calling")
      rp(this.state.authEndpoint + "?response_type=code&client_id=" + this.state.clientId + "&redirect_uri=https://developers.google.com/oauthplayground&scope=read write")
        .then(function (res) {
          console.log(res);
        })
        .catch(function (err) {
          console.log (err);
        });

    }

    this.setState({
      stage: this.state.stage + 1
    });
  }

  getToken() {
    const authCode = this.state.authCode;
  }
}

export default App;
