import React from 'react';
import DetailsForm from './DetailsForm';
import './App.css';
import rp from 'request-promise';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      stage: 0,
      clientId: '1x46iftmdzqtt',
      clientSecret: '4ctdx7k5du36r5q1qfkarhw2ts5tbbhyv13c8yar0es6k40cwg',
      authEndpoint: 'https://api.capsulecrm.com/oauth/authorise',
      tokenEndpoint: 'https://api.capsulecrm.com/oauth/token'
    };

    this.updateState = this.updateState.bind(this);
    this.nextStage = this.nextStage.bind(this);
  }

  render() {

    var main;

    switch (this.state.stage) {
      case 0: 
        main = <DetailsForm changeHandler={this.updateState} submitHandler={this.nextStage}/>;
        break;

      case 1:
        main = <p>woo</p>;
        break;

    }

    return (
      <div className="App">
        <header className="App-header"/>
        <body>
          <div className="container center">
            {main}
          </div>
        </body>
      </div>
    );
  }

  updateState(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }

  nextStage() {
    if (this.state.stage == 0) {
      console.log("calling")
      rp(this.state.authEndpoint + "?response_type=code&client_id=" + this.state.clientId + "&redirect_uri=https://developers.google.com/oauthplayground&scope=read write")
        .then(function (res) {
          console.log(res);
        })
        .catch(function (err) {
          console.log (err);
        });

    }

    this.setState({
      stage: this.state.stage + 1
    });
  }
}

export default App;
