import React from 'react';
import DetailsForm from './DetailsForm';
import './App.css';
import rp from 'request-promise';
import { throwStatement } from '@babel/types';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      stage: 0,
      clientId: '1x46iftmdzqtt',
      clientSecret: '4ctdx7k5du36r5q1qfkarhw2ts5tbbhyv13c8yar0es6k40cwg',
      authEndpoint: 'https://api.capsulecrm.com/oauth/authorise',
      tokenEndpoint: 'https://api.capsulecrm.com/oauth/token',
      authCode: ''
    };

    this.updateState = this.updateState.bind(this);
    this.nextStage = this.nextStage.bind(this);
    this.getToken = this.getToken.bind(this);
  }

  render() {

    const search = window.location.search;
    const params = new URLSearchParams(search);
    this.state.authCode = params.get('code');

    var main;

    if (this.state.authCode) {
      main = 
        <div>
          <h1>Your auth code is: </h1>
          <p>asdasd</p>
        <button onClick={this.getToken} className="button" value="Access Token"/>

        </div> 

    } else {
      main = <DetailsForm changeHandler={this.updateState} submitHandler={this.nextStage}/>;
    }

    return (
      <div className="App">
        <header className="App-header"/>
        <body>
          <div className="container center">
            {main}
          </div>
        </body>
      </div>
    );
  }

  updateState(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }

  nextStage() {
    if (this.state.stage == 0) {
      console.log("calling")
      window.href.location = this.state.authEndpoint + "?response_type=code&client_id=" + this.state.clientId + "&redirect_uri=https://developers.google.com/oauthplayground&scope=read write";
    }

    this.setState({
      stage: this.state.stage + 1
    });
  }

  getToken() {
    console.log("getting token")
    const authCode = this.state.authCode;

    const options = {
      uri: this.state.tokenEndpoint,
      method: 'POST',
      body: {
        code: authCode,
        client_id: this.state.clientId,
        client_secret: this.state.clientSecret,
        grant_type: "authorization_code"
      },
      json: true
    };

    rp.post(options)
      .then(function(res) {
        console.log(res);
      })
  }
}

export default App;
