import React from 'react';

class DetailsForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = props.state

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.props({
      clientId: event.target.clientId,
      clientSecret: event.target.clientSecret,
      authEndpoint: event.target.authEndpoint,
      tokenEndpoint: event.target.tokenEndpoint
    });
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Client ID:
          <input type="text" value={this.state.clientId} onChange={this.handleChange} />
        </label>

        <br/>

        <label>
          Client Secret:
          <input type="text" value={this.state.clientSecret} onChange={this.handleChange} />
        </label>
        <br/>

        <label>
          Auth endpoint:
          <input type="text" value={this.state.authEndpoint} onChange={this.handleChange} />
        </label>
        <br/>

        <label>
          Token endpoint:
          <input type="text" value={this.state.tokenEndpoint} onChange={this.handleChange} />
        </label>
      </form>
    );
  }
}

export default DetailsForm;
