import React from 'react';
import logo from './logo.svg';
import DetailsForm from './DetailsForm';
import './App.css';
import { throwStatement } from '@babel/types';

function App() {

  const state = {
    clientId: '',
    clientSecret: '',
    authEndpoint: '',
    tokenEndpoint: ''
  }

  return (
    <div className="App">
      <header className="App-header">


        <p>Client ID: {state.clientId}</p>


        <DetailsForm changeHandler={updateState} stateProvider={getState}/>

      </header>
    </div>
  );



  function updateState(state) {
    state = state;
  }

  function getState() {
    return state;
  }
}

export default App;
