import React from 'react';
import { FormGroup, InputGroup, Button } from '@blueprintjs/core';

class DetailsForm extends React.Component {
  constructor(props) {
    super(props);

    this.handleChange = props.changeHandler;
    this.handleSubmit = props.submitHandler;
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
          <FormGroup
              label='Client ID'
              labelFor='text-input'
              labelInfo='(required)'>
            <InputGroup id='text-input' placeholder='1x46iftmdzqtt'></InputGroup>
          </FormGroup>

          <FormGroup
              label='Client Secret'
              labelFor='text-input'
              labelInfo='(required)'>
            <InputGroup id='text-input' placeholder='4ctdx7k5du36r5q1qfkarhw2ts5tbbhyv13c8yar0es6k40cwg'></InputGroup>
          </FormGroup>

          <FormGroup
              helperText='The endpoint used by the client to obtain an authorisation code.'
              label='Auth Endpoint'
              labelFor='text-input'
              labelInfo='(required)'>
            <InputGroup id='text-input' placeholder='https://my.domain/oauth/authorise'></InputGroup>
          </FormGroup>

          <FormGroup
              helperText='The endpoint used by the application to trade an auth code for an access token.'
              label='Token Endpoint'
              labelFor='text-input'
              labelInfo='(required)'>
            <InputGroup id='text-input' placeholder='https://my.domain/oauth/token'></InputGroup>
          </FormGroup>

          <Button rightIcon="arrow-right" intent="success" text="Next step" />
      </form>
    );
  }
}

export default DetailsForm;
