import React from 'react';
import './App.css';
import rp from 'request-promise';
import DetailsForm from './DetailsForm';

import { Card, Elevation, Button, Code } from '@blueprintjs/core';

class App extends React.Component {
  constructor(props) {
    super(props);
    const search = window.location.search;
    const params = new URLSearchParams(search);

    this.state = {
      clientId: '1x46iftmdzqtt',
      clientSecret: '4ctdx7k5du36r5q1qfkarhw2ts5tbbhyv13c8yar0es6k40cwg',
      authEndpoint: 'https://api.capsulecrm.com/oauth/authorise',
      tokenEndpoint: 'https://api.capsulecrm.com/oauth/token',
      authCode: params.get('code'),
      homeUrl: 'https://40807517.ngrok.io/'
    };

    this.updateState = this.updateState.bind(this);
    this.nextStage = this.nextStage.bind(this);
    this.getToken = this.getToken.bind(this);
  }

  render() {
    var main;

    if (this.state.authCode) {
      main = 
        <Card interactive={false} elevation={Elevation.ONE}>
          <h4>Your authorisation code is: </h4>
          <Code>{this.state.authCode}</Code>
          <Button onClick={this.getToken}>Submit</Button>
        </Card>
    } else {
      main =
        <DetailsForm 
          changeHandler={this.updateState} 
          submitHandler={this.nextStage}/>
    }

    return (
      <div className="App bp3-dark">
        <div className="container center">
          {main}
        </div>
      </div>
    );
  }

  updateState(event) {
    console.log('asdsad')
    console.log(event)
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }

  nextStage(event) {
    event.preventDefault();
    const url = `${this.state.authEndpoint}?response_type=code&client_id=${this.state.clientId}&redirect_uri=${this.state.homeUrl}&scope=read write`
    console.log(url)
    window.location = url;
  }

  getToken() {
    const authCode = this.state.authCode;

    const options = {
      uri: this.state.tokenEndpoint,
      method: 'POST',
      body: {
        code: authCode,
        client_id: this.state.clientId,
        client_secret: this.state.clientSecret,
        grant_type: "authorization_code"
      },
      json: true
    };

    rp.post(options)
      .then(function(res) {
        console.log(res);
      })
  }
}

export default App;
