import React from 'react';
import logo from './logo.svg';
import DetailsForm from './DetailsForm';
import './App.css';
import { throwStatement } from '@babel/types';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      clientId: '',
      clientSecret: '',
      authEndpoint: '',
      tokenEndpoint: ''
    };

    this.updateState = this.updateState.bind(this);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">


          <p>Client ID: {this.state.clientId}</p>


          <DetailsForm changeHandler={this.updateState}/>

        </header>
      </div>
    );
  }

  updateState(event) {
    this.setState({
      clientId: event.target.clientId,
      clientSecret: event.target.clientSecret,
      oauthEndpoint: event.target.oauthEndpoint,
      tokenEndpoint: event.target.tokenEndpoint
    });
  }
}

export default App;
